<?php

namespace ASN1;

use Proton\IosReceiptParser\ASN1\Pkcs7UnverifiedParser;
use PHPUnit\Framework\TestCase;

class Pkcs7UnverifiedParserTest extends TestCase
{
    private const RECEIPT = '';
    private const EXPECTED_PAYLOAD = '';

    /**
     * @dataProvider readerProvider
     */
    public function testReadUsingOnlyTrustedCerts(Pkcs7UnverifiedParser $reader): void
    {
        $this->expectException(\Exception::class);
        $this->expectExceptionMessage('Cannot provide proper pkcs7 verification');
        $reader->readUsingOnlyTrustedCerts('');
    }

    /**
     * @dataProvider readerProvider
     */
    public function testReadUnverified(Pkcs7UnverifiedParser $reader): void
    {
        $payload = $reader->readUnverified(base64_decode(self::RECEIPT));
        $this->assertSame(self::EXPECTED_PAYLOAD, base64_encode($payload));
    }

    public function readerProvider(): array
    {
        return [
            [new Pkcs7UnverifiedParser()],
        ];
    }
}
